import React, { useState, useEffect, useCallback } from 'react';
import ReactDOM from 'react-dom';
import ReactDOMServer from 'react-dom/server';
import { mapValues } from 'lodash';
import { diffLines, formatLines } from 'unidiff';
import { parseDiff, Diff, Hunk, getChangeKey } from 'react-diff-view';
import { useConversations, Conversation } from './comments';
import 'react-diff-view/style/index.css';
import './style.css';
import { json } from 'body-parser';

const EMPTY_HUNKS = [];

function App() {
    const [oldCode, setOldCode] = useState('');
    const [newCode, setNewCode] = useState('');
    const [{ type, hunks }, setDiff] = useState({ type: '', hunks: EMPTY_HUNKS });

    const fetchCodeFromAPI = useCallback(async () => {
        try {
            console.log("HELLO");
            const response = await fetch("http://localhost:3001/api/codes");

            if (!response.ok) {
                console.log('Failed to fetch code from API');
                throw new Error('Failed to fetch code from API');
            }
            console.log("Data Received");

            const { oldCode, newCode } = await response.json();
            console.log(oldCode);
            setOldCode(oldCode);
            setNewCode(newCode);
        } catch (error) {
            console.error('Error fetching code:', error);
        }
    }, []);

    useEffect(() => {
        fetchCodeFromAPI();
    }, [fetchCodeFromAPI]);

    const generateDiff = useCallback(() => {
        if (typeof oldCode !== 'string' || typeof newCode !== 'string') {
            console.error('Old code or new code is not a string:', { oldCode, newCode });
            return;
        }

        if (oldCode && newCode) {
            const diffText = formatLines(diffLines(oldCode, newCode), { context: 3 });
            const [diff] = parseDiff(diffText, { nearbySequences: 'zip' });
            setDiff({ type: 'split', hunks: diff.hunks || EMPTY_HUNKS });
        }
    }, [oldCode, newCode]);

    useEffect(() => {
        generateDiff();
    }, [oldCode, newCode, generateDiff]);

    const [conversations, { initConversation, addComment }] = useConversations();
    const codeEvents = {
        onDoubleClick({ change }) {
            const key = getChangeKey(change);
            if (!conversations[key]) {
                initConversation(key);
            }
        },
    };

    const widgets = mapValues(conversations, ({ comments }, changeKey) => (
        <Conversation key={changeKey} changeKey={changeKey} comments={comments} onSubmitComment={addComment} />
    ));

    const serializeContentToHTML = useCallback(() => {
        const diffContent = (
            <Diff viewType="split" diffType={type} hunks={hunks || EMPTY_HUNKS}>
                {hunks => hunks.map(hunk => (
                    <Hunk key={hunk.content} hunk={hunk} codeEvents={codeEvents} widgets={widgets} />
                ))}
            </Diff>
        );

        const htmlContent = ReactDOMServer.renderToStaticMarkup(diffContent);

        const styles = `
            body { font-family: Arial, sans-serif; width:100%}
            .diff { display: flex; }
            .hunk { margin: 10px 0; }
            .insert { background-color: lightgreen; width:50%}
            .delete { background-color: lightcoral; width:50%}
        `;

        const fullHtml = `
            <!DOCTYPE html>
            <html lang="en">
            <head>
                <meta charset="UTF-8">
                <title>Generated Content</title>
                <style>
                    ${styles}
                </style>
                <link rel="stylesheet" href="https://unpkg.com/react-diff-view/style/index.css">
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/4.16.13/antd.min.css">
            </head>
            <body>
                ${htmlContent}
            </body>
            </html>
        `;

        console.log('Sending HTML content to server for saving:', fullHtml);
        fetch('http://localhost:3001/save-html', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ html: fullHtml, filename: 'generated_content.html' }),
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to save HTML content');
            }
            console.log('HTML content saved successfully');
        })
        .catch(error => {
            console.error('Error saving HTML:', error);
        });
    }, [type, hunks, codeEvents, widgets]);

    useEffect(() => {
        if (oldCode && newCode && hunks.length > 0) {
            serializeContentToHTML();
        }
    }, [oldCode, newCode, hunks, serializeContentToHTML]);

    return null; // No need to render anything on the webpage
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);

export default App;
