import React, { useState, useEffect } from 'react';
import { parseDiff, Diff, Hunk } from 'react-diff-view';
import { createTwoFilesPatch } from 'diff';
import 'react-diff-view/style/index.css';

const oldFileContent = `
Hello, world!
This is a diff example.
`;

const newFileContent = `
Hello, React!
This is a diff example.
`;

const oldFileName = 'file1.txt';
const newFileName = 'file1.txt';

// Create the unified diff string
const diffText = createTwoFilesPatch(oldFileName, newFileName, oldFileContent, newFileContent);

function App() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    try {
      const parsedDiff = parseDiff(diffText);
      setFiles(parsedDiff);
    } catch (error) {
      console.error('Error parsing diff:', error);
    }
  }, []);

  return (
    <div className="App">
      {files.length > 0 ? (
        files.map(({ oldPath, newPath, hunks }) => (
          <Diff key={oldPath} viewType="unified" diffType="modify" hunks={hunks}>
            {hunks.map(hunk => (
              <Hunk key={hunk.content} hunk={hunk} />
            ))}
          </Diff>
        ))
      ) : (
        <p>No differences found or error parsing diff.</p>
      )}
    </div>
  );
}

export default App;
