import React from 'react';
import { Diff, parseDiff } from 'react-diff-view';
import { createPatch } from 'diff';
import 'react-diff-view/style/index.css';

const CodeDifferentiator = ({ old, news }) => {
  const diff = createPatch('oldCode', 'newCode', old, news, '', '', { context: 3 });

  const files = parseDiff(diff);
  const hunks = files[0].hunks;

  if (!hunks || hunks.length === 0) {
    return <p>No differences</p>;
  }

  return <Diff hunks={hunks} />;
};

const App = () => {
  const oldcode = `
function MyComponent() {
  return <div>Hello, new world!</div>;
}`;

  const newcode = `
function MyComponent() {
  return <div>Hello, old world!</div>;
}`;

  return (
    <div className="App">
      <h1>Code Difference Viewer</h1>
      <CodeDifferentiator old={oldcode} news={newcode} />
    </div>
  );
};

export default App;
