import React, {useState, useCallback} from 'react';
import ReactDOM from 'react-dom';
import {mapValues} from 'lodash';
import {Input, Button} from 'antd';
import {diffLines, formatLines} from 'unidiff';
import {parseDiff, Diff, Hunk, getChangeKey} from 'react-diff-view';
import {useInput} from './hooks';
import {useConversations, Conversation} from './comments';

import 'antd/dist/reset.css';
import 'react-diff-view/style/index.css';
import './style.css';

const EMPTY_HUNKS = [];


function App() {
  const oldText = useInput('');
  const newText = useInput('');
  const [{ type, hunks }, setDiff] = useState({ type: '', hunks: EMPTY_HUNKS });

  const updateDiffText = useCallback(() => {
      const diffText = formatLines(diffLines(oldText.value, newText.value), { context: 3 });
      const [diff] = parseDiff(diffText, { nearbySequences: 'zip' });
      setDiff({ type: 'split', hunks: diff.hunks || EMPTY_HUNKS });
  }, [oldText.value, newText.value, setDiff]);

  const [conversations, { initConversation, addComment }] = useConversations();
  const codeEvents = {
      onDoubleClick({ change }) {
          const key = getChangeKey(change);
          if (!conversations[key]) {
              initConversation(key);
          }
      },
  };

  const widgets = mapValues(conversations, ({ comments }, changeKey) => (
      <Conversation key={changeKey} changeKey={changeKey} comments={comments} onSubmitComment={addComment} />
  ));
  const styles = `
            /* Add your custom styles here */
            body { font-family: Arial, sans-serif; }
            .diff { display: flex; }
            .hunk { margin: 10px 0; }
            .insert { background-color: lightgreen; }
            .delete { background-color: red; }
        `;
  const serializeContentToHTML = () => {
      const contentElement = document.getElementById('diff-view');
      if (!contentElement) {
          alert('Content not found');
          return;
      }
      const htmlContent = contentElement.innerHTML;
      const fullHtml = `
          <!DOCTYPE html>
          <html lang="en">
          <head>
              <meta charset="UTF-8">
              <title>Generated Content</title>
              <style>
                  ${styles}
              </style>
          </head>
          <body>
              ${htmlContent}
          </body>
          </html>
      `;
      const blob = new Blob([fullHtml], { type: 'text/html' });
      const a = document.createElement('a');
      a.href = URL.createObjectURL(blob);
      a.download = 'generated_content.html';
      a.click();
  };

  return (
      <div>
          <header className="header">
              <div className="input">
                  <Input.TextArea className="text" rows={5} placeholder="OLD CODE..." {...oldText} />
                  <Input.TextArea className="text" rows={5} placeholder="NEW CODE..." {...newText} />
              </div>
              <Button className="submit" type="primary" onClick={updateDiffText}>
                  GENERATE DIFFERENCE
              </Button>
              [if(oldText && newText)]
              <Button className="download-html" type="primary" onClick={serializeContentToHTML}>
                  DOWNLOAD GENERATED CONTENT AS HTML
              </Button>
          </header>
          <main id="diff-view">
              <Diff viewType="split" diffType={type} hunks={hunks || EMPTY_HUNKS}>
                  {hunks =>
                      hunks.map(hunk => (
                          <Hunk key={hunk.content} hunk={hunk} codeEvents={codeEvents} widgets={widgets} />
                      ))
                  }
              </Diff>
          </main>
      </div>
  );
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);

export default App;
