import React, { useState, useEffect, useCallback } from 'react';
import ReactDOM from 'react-dom';
import { formatLines, parseDiff, diffLines } from 'unidiff';
import { Diff, Hunk, getChangeKey } from 'react-diff-view';
import { mapValues } from 'lodash';
import { useConversations, Conversation } from './comments';

import 'react-diff-view/style/index.css';
import './style.css';

const EMPTY_HUNKS = [];

function App() {
    const [oldCode, setOldCode] = useState('');
    const [newCode, setNewCode] = useState('');
    const [{ type, hunks }, setDiff] = useState({ type: '', hunks: EMPTY_HUNKS });

    const fetchCodeFromAPI = useCallback(async () => {
        try {
            const response = await fetch('http://localhost:3001/api/codes');

            if (!response.ok) {
                console.log('Failed to fetch code from API');
                throw new Error('Failed to fetch code from API');
            }

            const { oldCode, newCode } = await response.json();
            setOldCode(oldCode);
            setNewCode(newCode);
        } catch (error) {
            console.error('Error fetching code:', error);
        }
    }, []);

    useEffect(() => {
        fetchCodeFromAPI();
    }, [fetchCodeFromAPI]);

    const generateDiff = useCallback(() => {
        if (oldCode && newCode) {
            const diffText = formatLines(diffLines(oldCode, newCode), { context: 3 });
            const [diff] = parseDiff(diffText, { nearbySequences: 'zip' });
            setDiff({ type: 'split', hunks: diff.hunks || EMPTY_HUNKS });
        }
    }, [oldCode, newCode]);

    useEffect(() => {
        generateDiff();
    }, [oldCode, newCode, generateDiff]);

    const [conversations, { initConversation, addComment }] = useConversations();

    const codeEvents = {
        onDoubleClick({ change }) {
            const key = getChangeKey(change);
            if (!conversations[key]) {
                initConversation(key);
            }
        },
    };

    const widgets = mapValues(conversations, ({ comments }, changeKey) => (
        <Conversation key={changeKey} changeKey={changeKey} comments={comments} onSubmitComment={addComment} />
    ));

    // No need to serializeContentToHTML or fetch to /save-html anymore

    return (
        null
    );
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);

export default App;
